package conditional_operators;

public class If_elsecondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20, b=15;
		if (a>b)
		{
			System.out.println("a is greater than b");
		}
		else 
		{
			System.out.println("a is less than b");
		}
	}
}
